export default {
    data() {
        return {
            show2: false,
        };
    },
    props: {
        show: {
            type: Boolean,
            default: false
        }
    },
    methods: {
        hideDialog() {
            this.show2 = false;
            this.$emit('update:show', false)
        },
    },
}
